css:
.top-bar__nav{ 
color: #a5003c;
} 
.header__nav a { 
    font-weight: bold;
} 
.top-bar a.top-bar__countdown { 
    font-weight: bold;
} 
.footer__title[aria-expanded] {
    font-weight: bold;
    color: #a5003c;
}
.footer {
    color: #a5003c;
}
.footer__title {
    font-weight: bold;
    color: #a5003c;
}
.footer__store {
    color: #a5003c;
    font-weight: bold;
}

.menu__list {
    padding-top: 20px;
}
.logo_menu_lateral {
    height: 60px;
    padding-top: 10px;
    padding-left: 45px;
}
.banner-slider {
    max-width: 85rem;
    max-height: calc(90rem*var(--banner-aspect-ratio));
}
.text_menu_lateral {
  padding-left: 20px
}
.btn-primary {
    color: #fff;
    background-color: #66bb6a;
    border-color: #66bb6a;
}
.btn.btn-primary.btn-block {
    background: #66bb6a;
    border: #66bb6a;
}
.product-card .prices__discount, .product-card .product-card__prices, .product-card h3, .products-carousel .product-card__prices, .products-carousel h3, .products-carousel strong {
    text-align: center;
}
.prices__value {
    color: #a5003c;
}
.header__nav {
    padding-top: 23px;
}
.product-card__name {
    font-size: 15px;
}
.header__search {
    border-color: pink;
    border-style: double;
}
.btn.btn-lg.btn-primary {
    background: #28a745;
}
.banner-slider__slides, .banner-slider__slides > li {
	min-height: 0rem;
}
.banner-slider__slides, .banner-slider__slides > li {
	min-height: 0rem; 
}
.mb-lg-5, .my-lg-5 {
    margin-bottom: 0rem;
    margin-top: 0rem;
}
.stamps > li {
    max-width: 450px;
}
.badge-secondary, a.badge-secondary:focus, a.badge-secondary:hover {
    background-color: #a5003c;
}
.checkout__info {
    border-left: .3rem solid #a5003c !important;
}
.summary__amount__row--total strong {
    color: #a5003c !important;
}
.prices--literal .prices__discount span, .prices--literal .prices__installments span {
    color: #a5003c;
}
.prices__installments {
  display: none;
}



/* Tablet and desktop */
@media (min-width: 767px) {
  .header {
  top: 50px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}
/* Mobile */
@media screen and (max-width: 480px) {
  .header {
    top: 29px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}


.prices__discount span {
  font-weight: bold !important;
  font-size: 120% !important;
}
